import React from "react";

class Formulario extends React.Component{
    constructor(){
        super();
        this.state={
            titulo:"",
            responsable:"",
            descripcion:"",
            prioridad:"low"
        }
    }
    render(){
        return (
            <div className="contenedorGeneral">
               <div className="contenedorFormulario">
                    <form className="formulario">
                        <div className="tituloFormulario">
                            <input type="text" value={this.state.titulo} placeholder="Titulo de tarea" name="titulo" className="inputTitulo" ></input>
                        </div>
                        <div className="responsableFormulario"> 
                            <input type="text" value={this.state.responsable} name="responsable" placeholder="Responsable de la tarea" className="inputResponsable"></input>
                        </div>
                        <div className="descripcionFormulario">
                            <textarea className="inputDescripcion" value={this.state.descripcion} placeholder="Detalles de la terea" name="descripcion"></textarea>
                        </div>
                        <div className="prioridadFormulario">
                            <select className="selectFormulario" name="prioridad" value={this.state.prioridad}>
                                <option> Low </option>
                                <option> Medium </option>
                                <option> High </option>
                            </select>
                        </div>
                        <div className="divBoton">
                            <button type="submit">Guardar</button>
                        </div>
                    </form>
               </div>
            </div>
        )
    }
}

export default Formulario;
