import React from "react";

class Formulario extends React.Component{
    constructor(){
        super();
        this.state={
            titulo:"",
            responsable:"",
            descripcion:"",
            prioridad:"low"
        }
    }
    render(){
        return (
            <div className="contenedorGeneral">
               <div className="contenedorFormulario">
                    <form className="formulario">
                        <div className="tituloFormulario">
                            <input type="text" value={this.state.titulo} placeholder="Titulo de tarea" className="inputTitulo" ></input>
                        </div>
                        <div className="responsableFormulario"> 
                            <input type="text" value={this.state.responsable} placeholder="Responsable de la tarea" className="inputResponsable"></input>
                        </div>
                        <div className="descripcionFormulario">
                            <input type=""></input>
                        </div>
                    </form>
               </div>
            </div>
        )
    }
}

export default Formulario;
