import React, { Component } from "react";
import Formulario from "../Formulario/Formulario.jsx";
import Card from "../card/Card"

import "./Todos.css"
class Todos extends Component{
    constructor(){
        super();
        this.state={
            todos:[]
            
        }
    }
    nuevoTodo=(todo)=>{
        this.setState({todos: [...this.state.todos,todo]})
    }

 render(){
     return (
         <div className="contenedorGeneral">
            <div className="contenedorFormulario">
                <Formulario nuevoTodo={this.nuevoTodo} />
            </div>
            <div className="contenedorTodo">
               {this.state.todos.map((todo, i)=> {
                return <Card todo={todo} key={i}  id={i}/>
               }
               )}
            </div>
         </div>
     )
 }
}
export default Todos;

function Tareas  () {
    const [tareas,setTareas]=useState(lista);

const agregarTarea=(titulo,descripcion)=>{
    const tareaNueva={
        titulo:titulo,
        descripcion:descripcion,
        id:tareas.length
    }
    console.log(tareaNueva.id)
    const actualizadas= [...tareas,tareaNueva];
    setTareas(actualizadas)
}
const eliminarTarea=(id)=>{
const t= tareas.filter(tarea=>tarea.id !== id)
setTareas(t)
}
return(
    <div>
        <Formulario agregarTarea={agregarTarea} /> 
        {tareas.map(tarea=>
        <Tarea 
        eliminar={eliminarTarea}
        tarea={tarea}
        key={tarea.id} 
        id={tarea.id} 
        />    
         )} 
    
    </div>
)
}
