import React from "react";
import "./Formulario.css"
class Formulario extends React.Component{
    constructor(props){
        super(props);
        this.state={
            titulo:"",
            responsable:"",
            descripcion:"",
            prioridad:"low",
            validacion: true
        }
        this.handleChange= this.handleChange.bind(this)
    }
    handleChange= (e)=>{
      this.setState({[e.target.name]: e.target.value})
    }

    submit = (e)=>{
        e.preventDefault();
        if(this.state.titulo.trim()!== ""){
            this.props.nuevoTodo(this.state)
            this.setState({titulo: ""})
            this.setState({responsable: ""})
            this.setState({descripcion: ""})
        }
    }
    render(){
        return (
            <div className="contenedorGeneral">
               <div className="contenedorFormulario">
                    <form className="formulario" onSubmit={this.submit}>
                        <div className="tituloFormulario">
                        <input type="text" className="input titulo" value={this.state.titulo} onChange={this.handleChange} name="titulo" placeholder="Titulo" ></input>
                        </div>
                        <div className="responsableFormulario"> 
                            <input type="text" value={this.state.responsable}  onChange={this.handleChange} name="responsable" placeholder="Responsable de la tarea" className="inputResponsable"></input>
                        </div>
                        <div className="descripcionFormulario">
                            <textarea className="inputDescripcion"  onChange={this.handleChange} value={this.state.descripcion} placeholder="Detalles de la terea" name="descripcion"></textarea>
                        </div>
                        <div className="prioridadFormulario">
                            <select className="selectFormulario"  onChange={this.handleChange} name="prioridad" value={this.state.prioridad}>
                                <option> Low </option>
                                <option> Medium </option>
                                <option> High </option>
                            </select>
                        </div>
                        <div className="divBoton">
                            <button className="botonGuardar" type="submit">Guardar</button>
                        </div>
                    </form>
               </div>
            </div>
        )
    }
}

export default Formulario;
